// 					</Routes>
// 				</>
// 			</Router>
// 		</ApolloProvider>
// 	);
// }

// export default App;

function App() {
	return (
		<ApolloProvider client={client}>
			<Router>
				<>
					<Navbar />
					<Routes>
						<Route path="/" element={<SearchBooks />} />
						<Route path="/saved" element={<SavedBooks />} />
						<Route
							path="*"
							element={<h1 className="display-2">Wrong page!</h1>}
						/>
					</Routes>
				</>
			</Router>
		</ApolloProvider>
	);
}

export default App;
